import{_ as l,o as t,c as i,a as e}from"./app.aec7eb12.js";const g=JSON.parse('{"title":"HTTP\u534F\u8BAE\u7684\u6F14\u53D8","description":"","frontmatter":{},"headers":[{"level":2,"title":"HTTP/1.1","slug":"http-1-1","link":"#http-1-1","children":[]},{"level":2,"title":"HTTP/2","slug":"http-2","link":"#http-2","children":[]},{"level":2,"title":"HTTP/3","slug":"http-3","link":"#http-3","children":[]}],"relativePath":"\u524D\u7AEF\u57FA\u7840/HTTP/HTTP\u534F\u8BAE\u6F14\u8FDB.md","lastUpdated":1670483514000}'),T={name:"\u524D\u7AEF\u57FA\u7840/HTTP/HTTP\u534F\u8BAE\u6F14\u8FDB.md"},r=e('<h1 id="http\u534F\u8BAE\u7684\u6F14\u53D8" tabindex="-1">HTTP\u534F\u8BAE\u7684\u6F14\u53D8 <a class="header-anchor" href="#http\u534F\u8BAE\u7684\u6F14\u53D8" aria-hidden="true">#</a></h1><h2 id="http-1-1" tabindex="-1">HTTP/1.1 <a class="header-anchor" href="#http-1-1" aria-hidden="true">#</a></h2><ul><li>\u4F7F\u7528TCP\u957F\u8FDE\u63A5\u6539\u5584\u4E86\u77ED\u8FDE\u63A5\u9020\u6210\u7684\u6027\u80FD\u6D88\u8017</li><li>\u652F\u6301\u7BA1\u9053\u7F51\u7EDC\u901A\u4FE1\uFF0C\u53EA\u8981\u4E00\u4E2A\u8BF7\u6C42\u53D1\u9001\u51FA\u53BB\u4E86\uFF0C\u5C31\u53EF\u4EE5\u53D1\u9001\u7B2C\u4E8C\u4E2A\uFF0C\u51CF\u5C11\u6574\u4F53\u7684\u54CD\u5E94\u65F6\u95F4\u3002</li></ul><p>\u6027\u80FD\u74F6\u9888\uFF1A</p><ul><li>\u8BF7\u6C42\u5934\u672A\u538B\u7F29\u5C31\u53D1\u9001\uFF0C<strong>\u9996\u90E8\u4FE1\u606F\u8D8A\u591A\u5EF6\u8FDF\u8D8A\u5927</strong>\uFF0C\u53EA\u80FD\u538B\u7F29<code>Body</code>\u90E8\u5206</li><li>\u53D1\u9001\u5197\u4F59\u7684\u9996\u90E8\u3002\u6BCF\u6B21\u4E92\u76F8\u53D1\u9001<strong>\u76F8\u540C\u7684\u9996\u90E8</strong>\u9020\u6210\u6D6A\u8D39\u3002</li><li>\u670D\u52A1\u7AEF\u662F\u987A\u5E8F\u54CD\u5E94\u7684\uFF0C\u53EF\u80FD\u9020\u6210\u961F\u5934\u963B\u585E</li><li>\u6CA1\u6709\u4F18\u5148\u7EA7\u63A7\u5236</li><li>\u8BF7\u6C42\u53EA\u80FD\u4ECE\u5BA2\u6237\u7AEF\u5F00\u59CB\uFF0C\u670D\u52A1\u5668\u53EA\u80FD\u88AB\u52A8\u8BF7\u6C42</li></ul><h2 id="http-2" tabindex="-1">HTTP/2 <a class="header-anchor" href="#http-2" aria-hidden="true">#</a></h2><p>HTTP/2\u662F\u57FA\u4E8EHTTPS\u7684\uFF0C\u4F7F\u7528\u5B89\u5168\u6027\u4E5F\u662F\u6709\u4FDD\u969C\u7684\u3002</p><ul><li>\u5934\u90E8\u538B\u7F29 <ul><li>\u5982\u679C\u540C\u65F6\u53D1\u51FA\u591A\u4E2A\u8BF7\u6C42\uFF0C\u4ED6\u4EEC\u7684\u5934\u662F\u4E00\u6837\u6216\u8005\u7C7B\u4F3C\u7684\uFF0C\u90A3\u4E48\u4F1A\u6D88\u9664\u91CD\u590D\u7684\u90E8\u5206</li><li>\u4E5F\u5C31\u662F\u6240\u8C13\u7684<code>HPACK</code>\u7B97\u6CD5\uFF1A\u5728\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u7AEF\u540C\u65F6\u7EF4\u62A4\u4E00\u5F20\u8868\uFF0C\u6240\u6709\u5B57\u6BB5\u90FD\u4F1A\u5B58\u5165\u8FD9\u5F20\u8868\uFF0C\u751F\u6210\u4E00\u4E2A\u7D22\u5F15\u53F7\uFF0C\u4EE5\u540E\u5C31\u4E0D\u53D1\u9001\u540C\u6837\u5B57\u6BB5\u4E86<strong>\u53EA\u53D1\u9001\u7D22\u5F15\u53F7</strong>\uFF0C\u8FD9\u6837\u5C31\u63D0\u9AD8\u901F\u5EA6\u4E86\u3002</li></ul></li><li>\u4E8C\u8FDB\u5236\u683C\u5F0F <ul><li>\u5168\u9762\u91C7\u7528\u4E86\u4E8C\u8FDB\u5236\u683C\u5F0F\uFF0C\u5934\u4FE1\u606F\u548C\u6570\u636E\u4F53\u90FD\u662F\u4E8C\u8FDB\u5236\uFF0C\u5E76\u4E14\u7EDF\u79F0\u4E3A\u5E27\uFF1A\u5934\u4FE1\u606F\u5E27\u548C\u6570\u636E\u5E27\uFF0C\u5BF9\u8BA1\u7B97\u673A\u53CB\u597D\uFF0C\u65E0\u9700\u518D\u5C06\u660E\u6587\u7684\u62A5\u6587\u8F6C\u6362\u6210\u4E8C\u8FDB\u5236\uFF0C\u800C\u662F<strong>\u76F4\u63A5\u89E3\u6790\u4E8C\u8FDB\u5236\u62A5\u6587\uFF0C\u63D0\u9AD8\u4E86\u6570\u636E\u4F20\u8F93\u7684\u6548\u7387</strong></li></ul></li><li>\u6570\u636E\u6D41 <ul><li>HTTP/2\u4E2D\u7684\u6570\u636E\u5305\u4E0D\u662F\u6309\u987A\u5E8F\u53D1\u9001\u7684\uFF0C\u540C\u4E00\u8FDE\u63A5\u91CC\u8FDE\u7EED\u7684\u6570\u636E\u5305\uFF0C\u53EF\u80FD\u5C5E\u4E8E\u4E0D\u7528\u7684\u54CD\u5E94\u3002\u56E0\u6B64\u5FC5\u987B\u8981\u5BF9\u6570\u636E\u5305\u8FDB\u884C\u6807\u8BB0\uFF0C\u6307\u51FA\u5B83\u5C5E\u4E8E\u54EA\u4E2A\u54CD\u5E94\u3002</li><li>\u6BCF\u4E2A\u8BF7\u6C42\u6216\u54CD\u5E94\u7684\u6240\u6709\u6570\u636E\u5305\uFF0C\u79F0\u4E3A\u4E00\u4E2A<code>\u6570\u636E\u6D41</code>\u3002\u6BCF\u4E2A\u6570\u636E\u6D41\u90FD\u6807\u8BB0\u7740\u4E00\u4E2A\u72EC\u4E00\u65E0\u4E8C\u7684\u7F16\u53F7\uFF0C\u5176\u4E2D\u89C4\u5B9A<strong>\u5BA2\u6237\u7AEF\u53D1\u51FA\u7684\u6570\u636E\u6D41\u7F16\u53F7\u4E3A\u5947\u6570\uFF0C\u670D\u52A1\u5668\u53D1\u51FA\u7684\u6570\u636E\u6D41\u7F16\u53F7\u4E3A\u5076\u6570</strong>\u3002</li><li><strong>\u5BA2\u6237\u7AEF\u8FD8\u53EF\u4EE5\u6307\u5B9A\u6570\u636E\u6D41\u7684\u4F18\u5148\u7EA7</strong>\u3002\u4F18\u5148\u7EA7\u9AD8\u7684\u8BF7\u6C42\uFF0C\u670D\u52A1\u5668\u5C31\u5148\u54CD\u5E94\u8BE5\u8BF7\u6C42\u3002</li></ul></li><li>\u591A\u8DEF\u590D\u7528 <ul><li>HTTP/2\u662F\u53EF\u4EE5\u5728\u4E00\u4E2A\u8FDE\u63A5\u4E2D\u5E76\u53D1\u591A\u4E2A\u8BF7\u6C42\u6216\u54CD\u5E94\uFF0C\u800C\u4E0D\u7528\u6309\u7167\u987A\u5E8F\u4E00\u4E00\u5BF9\u5E94\u3002</li><li>\u79FB\u9664\u4E86HTTP/1.1\u4E2D\u4E32\u884C\u8BF7\u6C42\uFF0C\u4E0D\u9700\u8981\u6392\u961F\u7B49\u5F85\uFF0C<strong>\u964D\u4F4E\u4E86\u5EF6\u8FDF\uFF0C\u5927\u5E45\u5EA6\u63D0\u9AD8\u4E86\u8FDE\u63A5\u7684\u5229\u7528\u7387</strong>\u3002</li><li>\u4E3E\u4F8B\u6765\u8BF4\uFF0C\u5728\u4E00\u4E2A TCP \u8FDE\u63A5\u91CC\uFF0C\u670D\u52A1\u5668\u6536\u5230\u4E86\u5BA2\u6237\u7AEF A \u548C B \u7684\u4E24\u4E2A\u8BF7\u6C42\uFF0C\u5982\u679C\u53D1\u73B0 A \u5904\u7406\u8FC7\u7A0B\u975E\u5E38\u8017\u65F6\uFF0C\u4E8E\u662F\u5C31 \u56DE\u5E94 A \u8BF7\u6C42\u5DF2\u7ECF\u5904\u7406\u597D\u7684\u90E8\u5206\uFF0C\u63A5\u7740\u56DE\u5E94 B \u8BF7\u6C42\uFF0C\u5B8C\u6210\u540E\uFF0C\u518D\u56DE\u5E94 A \u8BF7\u6C42\u5269\u4E0B\u7684\u90E8\u5206\u3002</li></ul></li><li>\u670D\u52A1\u5668\u63A8\u9001 <ul><li>\u53EF\u4EE5\u4E3B\u52A8\u5411\u5BA2\u6237\u7AEF\u53D1\u9001\u6D88\u606F\u3002</li><li>\u4E3E\u4F8B\u6765\u8BF4\uFF0C\u5728\u6D4F\u89C8\u5668\u521A\u8BF7\u6C42 HTML \u7684\u65F6\u5019\uFF0C\u5C31\u63D0\u524D\u628A\u53EF\u80FD\u4F1A\u7528\u5230\u7684 JS\u3001CSS \u6587\u4EF6\u7B49\u9759\u6001\u8D44\u6E90\u4E3B\u52A8\u53D1\u7ED9\u5BA2\u6237\u7AEF\uFF0C\u51CF\u5C11\u5EF6\u65F6\u7684\u7B49\u5F85\uFF0C\u4E5F\u5C31\u662F<code>\u670D\u52A1\u5668\u63A8\u9001</code>(Server Push\uFF0C\u4E5F\u53EB Cache Push)\u3002</li></ul></li></ul><h2 id="http-3" tabindex="-1">HTTP/3 <a class="header-anchor" href="#http-3" aria-hidden="true">#</a></h2><p>HTTP2\u4E3B\u8981\u95EE\u9898\u5728\u4E8E\uFF0C\u591A\u4E2AHTTP\u8BF7\u6C42\u5728\u590D\u7528\u4E00\u4E2ATCP\u8FDE\u63A5\uFF0C\u4E0B\u5C42\u7684TCP\u534F\u8BAE\u4E0D\u6E05\u695A\u6709\u591A\u5C11\u4E2AHTTP\u8BF7\u6C42\u3002<strong>\u4E00\u65E6\u53D1\u751F\u4E22\u5305\uFF0C\u5C31\u4F1A\u89E6\u53D1TCP\u7684\u91CD\u4F20\u673A\u5236</strong>\uFF0C\u8FD9\u6837\u5B50\u4E00\u4E2ATCP\u8FDE\u63A5\u4E2D\u7684\u6240\u6709HTTP\u8BF7\u6C42\u90FD\u5FC5\u987B<strong>\u7B49\u5F85</strong>\u8FD9\u4E2A\u4E22\u4E86\u7684\u5305\u88AB\u91CD\u65B0\u4F20\u56DE\u6765\u3002</p><ul><li>HTTP/1.1\u4E2D\u7BA1\u9053\u4F20\u8F93\u5982\u679C\u4E00\u4E2A\u8BF7\u6C42\u963B\u585E\uFF0C\u961F\u5217\u540E\u7684\u8BF7\u6C42\u4E5F\u90FD\u963B\u585E\u4E86</li><li>HTTP/2\u591A\u4E2A\u8BF7\u6C42\u590D\u7528\u540C\u4E2ATCP\u8FDE\u63A5\uFF0C\u4E00\u65E6\u53D1\u751F\u4E22\u5305\uFF0C\u5C31\u4F1A\u963B\u585E\u6240\u6709\u7684HTTP\u8BF7\u6C42\u3002</li></ul><p>\u8FD9\u4E9B\u90FD\u662F\u57FA\u4E8ETCP\u4F20\u8F93\u5C42\u7684\u95EE\u9898\uFF0C\u6240\u4EE5<strong>HTTP/3\u628AHTTP\u4E0B\u5C42\u7684TCP\u534F\u8BAE\u6539\u6210\u4E86UDP</strong>\uFF01</p><p>UDP \u53D1\u751F\u662F\u4E0D\u7BA1\u987A\u5E8F\uFF0C\u4E5F\u4E0D\u7BA1\u4E22\u5305\u7684\uFF0C\u6240\u4EE5\u4E0D\u4F1A\u51FA\u73B0 HTTP/1.1 \u7684\u961F\u5934\u963B\u585E \u548C HTTP/2 \u7684\u4E00\u4E2A\u4E22\u5305\u5168\u90E8\u91CD\u4F20\u95EE\u9898\u3002</p><p>\u4F46UDP\u662F\u4E0D\u53EF\u9760\u4F20\u8F93\u7684\uFF0C\u4F46\u57FA\u4E8EUDP\u7684<code>QUIC\u534F\u8BAE</code>\u53EF\u4EE5\u5B9E\u73B0\u7C7B\u4F3CTCP\u7684\u53EF\u9760\u6027\u4F20\u8F93\u3002</p><ul><li>QUIC\u534F\u8BAE\u6709\u81EA\u5DF1\u7684\u4E00\u5957\u673A\u5236\u6765\u4FDD\u8BC1\u4F20\u8F93\u7684\u53EF\u9760\u6027\u3002\u5F53\u67D0\u4E2A\u6D41\u53D1\u751F\u4E22\u5305\u65F6\u53EA\u4F1A\u963B\u585E\u8FD9\u4E2A\u6D41\uFF0C<strong>\u5176\u4ED6\u6D41\u4E0D\u4F1A\u53D7\u5230\u5F71\u54CD</strong></li><li>TLS3\u5347\u7EA7\u4E86\u6700\u65B0\u76841.3\u7248\u672C\uFF0C\u4F18\u5316\u4E86\u8FC7\u7A0B\uFF0C\u53EA\u9700\u8981 1 \u4E2A RTT \u5F80\u8FD4\u65F6\u5EF6\uFF0C\u4E5F\u5C31\u662F<strong>\u53EA\u9700\u8981 3 \u6B21\u63E1\u624B</strong>\uFF1B\u5934\u90E8\u538B\u7F29\u7B97\u6CD5\u4E5F\u5347\u7EA7\u4E3A<code>QPark</code></li><li>HTTPS\u8981\u5EFA\u7ACB\u4E00\u4E2A\u94FE\u63A5\uFF0C\u9700\u8981\u82B1\u8D396\u6B21\u4EA4\u4E92\uFF0C\u5148\u5EFA\u7ACB3\u6B21\u63E1\u624B\uFF0C\u7136\u540E\u662FTLS/1.3\u76843\u6B21\u63E1\u624B\u3002QUIC\u76F4\u63A5\u5C06\u4EE5\u5F80\u7684TCP\u548CTLS/1.3\u7684\u516D\u6B21\u4EA4\u4E92\u5408\u5E76\u62103\u6B21\uFF0C\u51CF\u5C11\u4E86\u4EA4\u4E92\u6B21\u6570\u3002</li></ul><p>\u6240\u4EE5\uFF0C QUIC \u662F\u4E00\u4E2A\u5728 UDP \u4E4B\u4E0A\u7684<code>\u4F2A TCP + TLS + HTTP/2</code> \u7684\u591A\u8DEF\u590D\u7528\u7684\u534F\u8BAE\u3002</p><p>QUIC \u662F\u65B0\u534F\u8BAE\uFF0C\u5BF9\u4E8E\u5F88\u591A\u7F51\u7EDC\u8BBE\u5907\uFF0C\u6839\u672C\u4E0D\u77E5\u9053\u4EC0\u4E48\u662F QUIC\uFF0C\u53EA\u4F1A\u5F53\u505A UDP\uFF0C\u8FD9\u6837\u4F1A\u51FA\u73B0\u65B0\u7684\u95EE\u9898\u3002\u6240\u4EE5HTTP/3 \u73B0\u5728\u666E\u53CA\u7684\u8FDB\u5EA6\u975E\u5E38\u7684\u7F13\u6162\uFF0C\u4E0D\u77E5\u9053\u672A\u6765 UDP \u662F\u5426\u80FD\u591F\u9006\u88AD TCP\u3002</p><p><img src="https://cdn.jsdelivr.net/gh/Merlin218/image-storage@master/picX/image.3bg4wj20mk3k.jpg" alt="image"></p>',18),o=[r];function a(s,n,P,d,h,c){return t(),i("div",null,o)}const u=l(T,[["render",a]]);export{g as __pageData,u as default};
