import{_ as e,o as a,c as r,a as i}from"./app.aec7eb12.js";const C=JSON.parse('{"title":"\u6570\u636E\u94FE\u8DEF\u5C42","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8C01\u5148\u53D1\uFF1F\u8C01\u540E\u53D1\uFF1F","slug":"\u8C01\u5148\u53D1-\u8C01\u540E\u53D1","link":"#\u8C01\u5148\u53D1-\u8C01\u540E\u53D1","children":[]},{"level":2,"title":"\u53D1\u7ED9\u8C01\u7684\uFF1F\u8C01\u5E94\u8BE5\u63A5\u6536\uFF1F","slug":"\u53D1\u7ED9\u8C01\u7684-\u8C01\u5E94\u8BE5\u63A5\u6536","link":"#\u53D1\u7ED9\u8C01\u7684-\u8C01\u5E94\u8BE5\u63A5\u6536","children":[{"level":3,"title":"ARP\u534F\u8BAE","slug":"arp\u534F\u8BAE","link":"#arp\u534F\u8BAE","children":[]},{"level":3,"title":"\u4F20\u8F93\u4E2D\u4ECB","slug":"\u4F20\u8F93\u4E2D\u4ECB","link":"#\u4F20\u8F93\u4E2D\u4ECB","children":[]}]},{"level":2,"title":"\u53D1\u9001\u7684\u65F6\u5019\u51FA\u73B0\u4E86\u9519\u8BEF\uFF0C\u600E\u4E48\u529E\uFF1F","slug":"\u53D1\u9001\u7684\u65F6\u5019\u51FA\u73B0\u4E86\u9519\u8BEF-\u600E\u4E48\u529E","link":"#\u53D1\u9001\u7684\u65F6\u5019\u51FA\u73B0\u4E86\u9519\u8BEF-\u600E\u4E48\u529E","children":[]}],"relativePath":"\u524D\u7AEF\u57FA\u7840/\u8BA1\u7B97\u673A\u7F51\u7EDC/\u6570\u636E\u94FE\u8DEF\u5C42.md","lastUpdated":1670483514000}'),t={name:"\u524D\u7AEF\u57FA\u7840/\u8BA1\u7B97\u673A\u7F51\u7EDC/\u6570\u636E\u94FE\u8DEF\u5C42.md"},d=i('<h1 id="\u6570\u636E\u94FE\u8DEF\u5C42" tabindex="-1">\u6570\u636E\u94FE\u8DEF\u5C42 <a class="header-anchor" href="#\u6570\u636E\u94FE\u8DEF\u5C42" aria-hidden="true">#</a></h1><p>\u8BE5\u5C42\u4E5F\u662FMAC\u5C42\u3002MAC \u7684\u5168\u79F0\u662F<code>Medium Access Control</code>\uFF0C\u5373\u5A92\u4F53\u8BBF\u95EE\u63A7\u5236\u3002\u63A7\u5236\u4EC0\u4E48\u5462\uFF1F\u5176\u5B9E\u5C31\u662F<strong>\u63A7\u5236\u5728\u5F80\u5A92\u4F53\u4E0A\u53D1\u6570\u636E\u7684\u65F6\u5019\uFF0C\u8C01\u5148\u53D1\u3001\u8C01\u540E\u53D1\u7684\u95EE\u9898</strong>\u3002\u9632\u6B62\u53D1\u751F\u6DF7\u4E71\u3002</p><p>\u9700\u8981\u89E3\u51B3\u51E0\u4E2A\u95EE\u9898\uFF1A</p><ol><li>\u5927\u5BB6\u90FD\u5728\u53D1\uFF0C\u4F1A\u4E0D\u4F1A\u4EA7\u751F\u6DF7\u4E71\uFF1F\u6709\u6CA1\u6709\u8C01\u5148\u53D1\u3001\u8C01\u540E\u53D1\u7684\u89C4\u5219\uFF1F</li><li>\u8FD9\u4E2A\u5305\u662F\u53D1\u7ED9\u8C01\u7684\uFF1F\u8C01\u5E94\u8BE5\u63A5\u6536\uFF1F</li><li>\u5982\u679C\u53D1\u9001\u7684\u65F6\u5019\u51FA\u73B0\u4E86\u9519\u8BEF\uFF0C\u600E\u4E48\u529E\uFF1F</li></ol><h2 id="\u8C01\u5148\u53D1-\u8C01\u540E\u53D1" tabindex="-1">\u8C01\u5148\u53D1\uFF1F\u8C01\u540E\u53D1\uFF1F <a class="header-anchor" href="#\u8C01\u5148\u53D1-\u8C01\u540E\u53D1" aria-hidden="true">#</a></h2><p>\u8FD9\u4E2A\u95EE\u9898\u4E2D\u7684\u89C4\u5219\uFF0C\u5B66\u540D\u53EB<code>\u591A\u8DEF\u8BBF\u95EE</code>\u3002\u6709\u5F88\u591A\u7B97\u6CD5\u53EF\u4EE5\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\u3002</p><p>\u4E3B\u8981\u6709\u8FD9\u4E09\u79CD\u65B9\u5F0F\uFF1A</p><ul><li>\u4FE1\u9053\u5212\u5206\uFF1A\u5206\u591A\u4E2A\u4FE1\u9053\uFF0C\u5404\u4E0D\u5E72\u6270</li><li>\u8F6E\u6D41\u534F\u8BAE\uFF1A\u8F6E\u6D41\u4EA4\u66FF\u8FDB\u884C\u4F20\u8F93</li><li>\u968F\u673A\u63A5\u5165\u534F\u8BAE\uFF1A\u9519\u5CF0\u4F20\u8F93\uFF0C\u7528\u4E8E\u4EE5\u592A\u7F51</li></ul><h2 id="\u53D1\u7ED9\u8C01\u7684-\u8C01\u5E94\u8BE5\u63A5\u6536" tabindex="-1">\u53D1\u7ED9\u8C01\u7684\uFF1F\u8C01\u5E94\u8BE5\u63A5\u6536\uFF1F <a class="header-anchor" href="#\u53D1\u7ED9\u8C01\u7684-\u8C01\u5E94\u8BE5\u63A5\u6536" aria-hidden="true">#</a></h2><p>MAC\u5C42\u89C4\u5B9A\u4E86\u4E00\u4E2A\u7F51\u7EDC\u5305\u7684\u683C\u5F0F\u3002</p><p>\u7528\u5230\u4E86\u4E00\u4E2A\u7269\u7406\u5730\u5740\uFF0C\u53EB\u505A<code>\u94FE\u8DEF\u5C42\u5730\u5740</code>\uFF0C\u901A\u5E38\u6211\u4EEC\u79F0\u4E3A<code>MAC\u5730\u5740</code>\u3002</p><p><img src="https://cdn.jsdelivr.net/gh/Merlin218/image-storage/picGo/202209191120189.png" alt=""></p><p>\u7C7B\u578B\u5927\u90E8\u5206\u90FD\u662FIP\u6570\u636E\u5305\uFF0C\u5305\u542B\u4E86TCP\u3001UDP\u3001HTTP\u7B49\u5185\u5BB9\u3002</p><p>\u6709\u4E86\u8FD9\u4E2A\u76EE\u6807 MAC \u5730\u5740\uFF0C\u6570\u636E\u5305\u5728\u94FE\u8DEF\u4E0A\u5E7F\u64AD\uFF0CMAC \u7684\u7F51\u5361\u5C31\u80FD\u53D1\u73B0\uFF0C\u54EA\u4E2A\u5305\u5C5E\u4E8E\u81EA\u5DF1\u3002</p><p>\u8FD9\u91CC\u8FD8\u6709\u4E00\u4E2A\u6CA1\u6709\u89E3\u51B3\u7684\u95EE\u9898\uFF0C\u6E90\u673A\u5668\u5982\u679C\u4E0D\u77E5\u9053\u76EE\u6807MAC\u5730\u5740\uFF0C\u5B83\u5982\u4F55\u83B7\u5F97\u76EE\u6807IP\u5730\u5740\u7684MAC\u5730\u5740\u5462\uFF1F</p><p>\u8FD9\u5C31\u9700\u8981ARP \u534F\u8BAE\uFF0C\u4E5F\u5C31\u662F<strong>\u5DF2\u77E5 IP \u5730\u5740\uFF0C\u6C42 MAC \u5730\u5740\u7684\u534F\u8BAE</strong>\u3002</p><h3 id="arp\u534F\u8BAE" tabindex="-1">ARP\u534F\u8BAE <a class="header-anchor" href="#arp\u534F\u8BAE" aria-hidden="true">#</a></h3><p>\u5F53\u5305\u53D1\u5230\u4E00\u4E2A<strong>\u5C40\u57DF\u7F51</strong>\u91CC\uFF0C\u77E5\u9053IP\u5730\u5740\uFF0C\u6B64\u65F6\u5C31\u4F1A\u53D1\u4E00\u4E2A\u5E7F\u64AD\u5305\uFF0C\u95EE\u6240\u6709\u673A\u5668\uFF0C\u8C01\u7684IP\u5730\u5740\u662F\u76EE\u7684\u5730\u5740\uFF0C\u6211\u9700\u8981\u4F60\u7684MAC\u5730\u5740\u3002\u5177\u4F53\u7684\u62A5\u6587\u683C\u5F0F\u5982\u4E0B\uFF1A</p><p><img src="https://cdn.jsdelivr.net/gh/Merlin218/image-storage/picGo/202209191133482.png" alt=""></p><p>\u5F53\u76EE\u6807\u4E3B\u673A\u63A5\u6536\u5230\u5E7F\u64AD\u62A5\u6587\u65F6\uFF0C\u5C31\u4F1A\u56DE\u5E94\u4E00\u4E2AARP\u5E94\u7B54\u62A5\u6587\u3002</p><p>\u4E3A\u4E86\u907F\u514D\u6BCF\u6B21\u90FD\u7528 ARP \u8BF7\u6C42\uFF0C\u673A\u5668\u672C\u5730\u4E5F\u4F1A\u8FDB\u884C ARP \u7F13\u5B58\u3002\u5F53\u7136\u673A\u5668\u4F1A\u4E0D\u65AD\u5730\u4E0A\u7EBF\u4E0B\u7EBF\uFF0CIP \u4E5F\u53EF\u80FD\u4F1A\u53D8\uFF0C\u6240\u4EE5 ARP \u7684 MAC \u5730\u5740\u7F13\u5B58\u8FC7\u4E00\u6BB5\u65F6\u95F4\u5C31\u4F1A\u8FC7\u671F\u3002</p><p>\u8FD9\u91CC\u53C8\u6D89\u53CA\u5230\u4F20\u8F93\u4E2D\u4ECB\u8BBE\u5907\uFF1A</p><h3 id="\u4F20\u8F93\u4E2D\u4ECB" tabindex="-1">\u4F20\u8F93\u4E2D\u4ECB <a class="header-anchor" href="#\u4F20\u8F93\u4E2D\u4ECB" aria-hidden="true">#</a></h3><h4 id="\u96C6\u7EBF\u5668" tabindex="-1">\u96C6\u7EBF\u5668 <a class="header-anchor" href="#\u96C6\u7EBF\u5668" aria-hidden="true">#</a></h4><p>\u8FD9\u79CD\u8BBE\u5907\u6709\u591A\u4E2A\u53E3\uFF0C\u53EF\u4EE5\u5C06\u5BBF\u820D\u91CC\u7684\u591A\u53F0\u7535\u8111\u8FDE\u63A5\u8D77\u6765\u3002\u4F46\u662F\uFF0C\u548C\u4EA4\u6362\u673A\u4E0D\u540C\uFF0C\u96C6\u7EBF\u5668\u6CA1\u6709\u5927\u8111\uFF0C\u5B83\u5B8C\u5168\u5728\u7269\u7406\u5C42\u5DE5\u4F5C\u3002<strong>\u5B83\u4F1A\u5C06\u81EA\u5DF1\u6536\u5230\u7684\u6BCF\u4E00\u4E2A\u5B57\u8282\uFF0C\u90FD\u590D\u5236\u5230\u5176\u4ED6\u7AEF\u53E3\u4E0A\u53BB\u3002</strong> \u8FD9\u662F\u7B2C\u4E00\u5C42\u7269\u7406\u5C42\u8054\u901A\u7684\u65B9\u6848\u3002</p><h4 id="\u4EA4\u6362\u673A" tabindex="-1">\u4EA4\u6362\u673A <a class="header-anchor" href="#\u4EA4\u6362\u673A" aria-hidden="true">#</a></h4><p>\u5F53\u4E3B\u673A\u6570\u4E00\u591A\uFF0C\u5C31\u5BB9\u6613\u53D1\u751F\u51B2\u7A81\u4E86\uFF0C\u800C\u4E14\u628A\u4E0D\u9700\u8981\u7684\u5305\u8F6C\u51FA\u53BB\u7EAF\u5C5E\u6D6A\u8D39\u3002</p><p>\u6B64\u65F6\u5C31\u9700\u8981\u4E00\u4E2A\u201C\u66F4\u667A\u80FD\u201D\u7684\u4E2D\u4ECB\uFF0C\u6765\u7BA1\u7406\u62A5\u6587\u7684\u8F6C\u53D1\u3002\u56E0\u4E3A\u6BCF\u4E2A\u53E3\u90FD\u53EA\u8FDE\u63A5\u4E00\u53F0\u7535\u8111\uFF0C\u8FD9\u53F0\u7535\u8111\u53C8\u4E0D\u600E\u4E48\u6362 IP \u548C MAC \u5730\u5740\uFF0C\u53EA\u8981\u8BB0\u4F4F\u8FD9\u53F0\u7535\u8111\u7684 MAC \u5730\u5740\uFF0C\u5982\u679C\u76EE\u6807 MAC \u5730\u5740\u4E0D\u662F\u8FD9\u53F0\u7535\u8111\u7684\uFF0C\u8FD9\u4E2A\u53E3\u5C31\u4E0D\u7528\u8F6C\u53D1\u4E86\u3002</p><p>\u4E00\u53F0 MAC1 \u7535\u8111\u5C06\u4E00\u4E2A\u5305\u53D1\u9001\u7ED9\u53E6\u4E00\u53F0 MAC2 \u7535\u8111\uFF0C\u5F53\u8FD9\u4E2A\u5305\u5230\u8FBE\u4EA4\u6362\u673A\u7684\u65F6\u5019\uFF0C\u4E00\u5F00\u59CB\u4EA4\u6362\u673A\u4E5F\u4E0D\u77E5\u9053 MAC2 \u7684\u7535\u8111\u5728\u54EA\u4E2A\u53E3\uFF0C\u6240\u4EE5\u6CA1\u529E\u6CD5\uFF0C\u5B83\u53EA\u80FD\u5C06\u5305\u8F6C\u53D1\u7ED9\u9664\u4E86\u6765\u7684\u90A3\u4E2A\u53E3\u4E4B\u5916\u7684\u5176\u4ED6\u6240\u6709\u7684\u53E3\u3002</p><p>\u4F46\u662F\uFF0C\u8FD9\u4E2A\u65F6\u5019\uFF0C\u4EA4\u6362\u673A\u4F1A\u5E72\u4E00\u4EF6\u975E\u5E38\u806A\u660E\u7684\u4E8B\u60C5\uFF0C\u5C31\u662F<strong>\u4EA4\u6362\u673A\u4F1A\u8BB0\u4F4F\uFF0CMAC1 \u662F\u6765\u81EA\u4E00\u4E2A\u660E\u786E\u7684\u53E3\u3002\u4EE5\u540E\u6709\u5305\u7684\u76EE\u7684\u5730\u5740\u662F MAC1 \u7684\uFF0C\u76F4\u63A5\u53D1\u9001\u5230\u8FD9\u4E2A\u53E3\u5C31\u53EF\u4EE5\u4E86\u3002</strong></p><p>\u8FC7\u4E86\u4E00\u6BB5\u65F6\u95F4\u4E4B\u540E\uFF0C\u5C31\u6709\u4E86\u6574\u4E2A\u7F51\u7EDC\u7684\u4E00\u4E2A\u7ED3\u6784\u4E86\uFF0C\u8FD9\u4E2A\u65F6\u5019\uFF0C\u57FA\u672C\u4E0A\u4E0D\u7528\u5E7F\u64AD\u4E86\uFF0C\u5168\u90E8\u53EF\u4EE5\u51C6\u786E\u8F6C\u53D1\u3002\u5F53\u7136\uFF0C\u6BCF\u4E2A\u673A\u5668\u7684 IP \u5730\u5740\u4F1A\u53D8\uFF0C\u6240\u5728\u7684\u53E3\u4E5F\u4F1A\u53D8\uFF0C\u56E0\u800C\u4EA4\u6362\u673A\u4E0A\u7684\u5B66\u4E60\u7684\u7ED3\u679C\uFF0C\u6211\u4EEC\u79F0\u4E3A<code>\u8F6C\u53D1\u8868</code>\uFF0C\u662F\u6709\u4E00\u4E2A\u8FC7\u671F\u65F6\u95F4\u7684\u3002</p><blockquote><ol><li><p>\u5728\u4E8C\u5C42\u4E2D\u6211\u4EEC\u8BB2\u4E86 ARP \u534F\u8BAE\uFF0C\u5373\u5DF2\u77E5 IP \u5730\u5740\u6C42 MAC\uFF1B\u8FD8\u6709\u4E00\u79CD RARP \u534F\u8BAE\uFF0C\u5373\u5DF2\u77E5 MAC \u6C42 IP \u7684\uFF0C\u4F60\u77E5\u9053\u5B83\u53EF\u4EE5\u7528\u6765\u5E72\u4EC0\u4E48\u5417\uFF1F</p></li><li><p>\u5982\u679C\u4E00\u4E2A\u5C40\u57DF\u7F51\u91CC\u9762\u6709\u591A\u4E2A\u4EA4\u6362\u673A\uFF0CARP \u5E7F\u64AD\u7684\u6A21\u5F0F\u4F1A\u51FA\u73B0\u4EC0\u4E48\u95EE\u9898\u5462\uFF1F</p></li></ol></blockquote><h2 id="\u53D1\u9001\u7684\u65F6\u5019\u51FA\u73B0\u4E86\u9519\u8BEF-\u600E\u4E48\u529E" tabindex="-1">\u53D1\u9001\u7684\u65F6\u5019\u51FA\u73B0\u4E86\u9519\u8BEF\uFF0C\u600E\u4E48\u529E\uFF1F <a class="header-anchor" href="#\u53D1\u9001\u7684\u65F6\u5019\u51FA\u73B0\u4E86\u9519\u8BEF-\u600E\u4E48\u529E" aria-hidden="true">#</a></h2><p>\u7F51\u7EDC\u5305\u4E2D\u6709\u4E00\u4E2A<code>CRC</code>\u7684\u90E8\u5206\uFF0C\u662F<code>\u5FAA\u73AF\u5197\u4F59\u68C0\u6D4B</code>\uFF0C\u901A\u8FC7 XOR \u5F02\u6216\u7684\u7B97\u6CD5\uFF0C\u6765\u8BA1\u7B97\u6574\u4E2A\u5305\u662F\u5426\u5728\u53D1\u9001\u7684\u8FC7\u7A0B\u4E2D\u51FA\u73B0\u4E86\u9519\u8BEF\uFF0C\u4E3B\u8981\u89E3\u51B3\u7B2C\u4E09\u4E2A\u95EE\u9898\u3002</p>',34),l=[d];function p(n,o,c,s,h,A){return a(),r("div",null,l)}const g=e(t,[["render",p]]);export{C as __pageData,g as default};
